Function downImg(hWeb, css, imgType, downPath)
    Dim fun = '''
    function(){ 
        // 参数设置
        var css = "''' 
    Dim fun1 = '''",imgType = "image/'''
    Dim fun2 = '''",postfix=".'''
    Dim fun3 = '''";
        // 获取验证码图片的元素
        var img = document.querySelector(css);
        // 创建画布
        var canvas = document.createElement("canvas");
        // 验证码图片，原始宽度
        canvas.width = img.naturalWidth;
        // 验证码图片，原始高度
        canvas.height = img.naturalHeight;
        // 2D模式
        var ctx = canvas.getContext("2d");
        // 渲染图片
        ctx.drawImage(img, 0, 0, img.naturalWidth, img.naturalHeight);
        // 转为base64，用于给a做文件下载
        var dataURL = canvas.toDataURL(imgType);
            // 生成一个a元素
        var a = document.createElement('a');
        // 创建一个单击事件，模拟下载
        var event = new MouseEvent('click');
        // 设置图片名称
        a.download = new Date().getTime() + postfix
        // 将生成的URL设置为a.href属性
        a.href = dataURL;
        // 触发a的单击事件
        a.dispatchEvent(event);
        return a.download;
    }
    '''
    Dim sRet = WebBrowser.RunJS(hWeb,fun & css & fun1 & imgType & fun2 & imgType & fun3 ,true,{"bContinueOnError":false,"iDelayAfter":1000,"iDelayBefore":200})
    return downPath & sRet
End Function
